BR.Model = false

function GetRandomModel()
	return sexList[math.random(1, #sexList)]
end

RegisterCommand("skin", function()
	if BR.Players[PlayerId()] then ChatNotif("You cannot use this command while ingame.") return end
	local randomModel = GetRandomModel()

	if RequestAndWaitModel(randomModel) then
		SetPlayerModel(PlayerId(), randomModel)

		ChatNotif("Model set to: " .. randomModel)
		BR.Model = randomModel
	end
end)

RegisterCommand("saveskin", function()
	if BR.Players[PlayerId()] then ChatNotif("You cannot use this command while ingame.") return end
	if not BR.Model or not IsModelInCdimage(BR.Model) then ChatNotif("Please use /skin before.") return end

	TriggerServerEvent("commandHandler", "saveskin", BR.Model)
end)

RegisterCommand("spectate", function()
	if BR.Players[PlayerId()] or BR.StartTime == 0 then ChatNotif("You cannot spectate a game that already started.") return end

	if BR.Status == 2 or NetworkIsInSpectatorMode() then
		BR.Spawn:SpawnPed()
		BR.Status = 0
	else
		BR.Spawn:SpawnPed()
		BR.Status = 2
	end
end)

